import React, {useState} from 'react';
import ExpenseFilter from './ExpenseFilter';
import ExpenseItem from './ExpenseItem';
import Card from '../UI/Card';
import './Expenses.css';

const Expenses = (props) => {
  const [selectedFilter, setChangeFilter]= useState('2020');

  const onChangeFilter=(yearSelected)=>{
    setChangeFilter(yearSelected);

    console.log(yearSelected);
  }
  const filteredExpense=props.items.filter(x=> x.date.getFullYear()==selectedFilter);
  return (
    <Card className="expenses">
      <ExpenseFilter selected={selectedFilter} onChangeFilter={onChangeFilter}/>
      {filteredExpense.map(expense=>(<ExpenseItem key={expense.id} title={expense.title} amount={expense.amount} date={expense.date}/>))}
    </Card>
  );
}

export default Expenses;
